const MILLISECONDS_PER_SECOND = 1000;
const SECONDS_PER_MINUTE = 60;

const SECOND = MILLISECONDS_PER_SECOND;
const MINUTE = SECOND * SECONDS_PER_MINUTE;

export const ONE_HUNDRED_MILLIS = 100;
export const QUARTER_SEC = SECOND / 4;
export const HALF_SEC = SECOND / 2;
export const ONE_SEC = SECOND;
export const TWO_SEC = SECOND * 2;
export const THREE_SEC = SECOND * 3;
export const FOUR_SEC = SECOND * 4;
export const FIVE_SEC = SECOND * 5;
export const TEN_SEC = SECOND * 10;
export const HALF_MIN = MINUTE / 2;
export const ONE_MIN = MINUTE;
export const TWO_MIN = MINUTE * 2;
export const THREE_MIN = MINUTE * 3;
export const FOUR_MIN = MINUTE * 4;
export const FIVE_MIN = MINUTE * 5;
export const TEN_MIN = MINUTE * 10;
export const TWENTY_MIN = MINUTE * 20;
